window["Paddle"] = function(renderableObj) {
    GameObject.call(this, renderableObj);
    this.mCircleBound = false;
    this.mCollidableFlag = true;
    this.mCollisionPixelFlag = false;
    this.mDestroy = false;
}
gEngine.Core.inheritPrototype(window["Paddle"], window["GameObject"]);

Paddle.prototype.update = function() {
    var xf = this.getXform();
    if (gEngine.Input.isKeyPressed(gEngine.Input.keys.Left)) {
        xf.setXPos(xf.getXPos() - 0.2);
    }
    if (gEngine.Input.isKeyPressed(gEngine.Input.keys.Right)) {
        xf.setXPos(xf.getXPos() + 0.2);
    }
    if (gEngine.Input.isKeyClicked(gEngine.Input.keys.Space)) {
        var ball = new Ball(new Renderable());
        var ballxf = ball.getXform();
        ballxf.setXPos(25);
        ballxf.setYPos(58);
        instantiate(ball);
    }
    GameObject.prototype.update.call(this);
};

Paddle.prototype.draw = function(aCamera) {
    GameObject.prototype.draw.call(this, aCamera);
};

